from Crypto.Util.number import *
import primefac
import gmpy2
import tqdm






for ep in primefac.primefac(p-1):
    if ep>2 and pow(c%p,(p-1)//ep,p)==1:
        break
for eq in primefac.primefac(q-1):
    if eq>2 and pow(c%q,(q-1)//eq,q)==1:
        break
for er in primefac.primefac(r-1):
    if er>2 and pow(c%r,(r-1)//er,r)==1:
        break

e=ep*eq*er
n=p*q*r
e0=ep*eq
n0=p*q
phi0=(p-1)*(q-1)
d1=gmpy2.invert(er,phi0)
c1=c*gmpy2.invert(pow(256,128*e,n),n)%n
c0=pow(c1,d1,n0)

#https://eprint.iacr.org/2020/1059.pdf
g0=pow(2,phi0//e0,n0)
d0=gmpy2.invert(e0,phi0//e0)
a0=pow(c0,d0,n0)
t=tqdm.tqdm(total=int(e0),ascii=True)
for i in range(e0):
    m=long_to_bytes(a0)
    if b'SUSCTF' in m:
        flag=m.decode()
        print(flag)
        t.close()
        break
    else:
        t.update()
    a0=a0*g0%n0